import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users"

  React.useEffect(()=>{
    fetch( url )
      .then( res => { return res.json()
      }).then( received => {
        console.log(received)
        setData(received)
        setLoaded(true)
      })
  }, [])


  const UserDataDiv = ({data}) => {
    console.log(data.data)
    return data.data.map( (user, index)=>{
      return (
      <div className="block container pt-3 mb-3 d-flex" >
        <div className="container m-0 pb-3 d-flex flex-column">
          <div className='row-6'>{user.title.charAt(0).toUpperCase()}{user.title.charAt(1)} {user.firstName} {user.lastName}</div>
          <img className='row-6' src={user.picture} style={{width:"100px"}}/>
        </div>
      </div>)
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"background-color": "#ffffff","margin":"0px","min-height":"80vh"}}>
          <div className="container pt-4" style={{color:"black"}}>
            {loaded?<UserDataDiv data={data}/>:<h1>loading...</h1>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
