import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users"

  React.useEffect(()=>{
    fetch( url )
      .then( res => { return res.json()
      }).then( received => {
        console.log(received)
        setData(received)
        setLoaded(true)
      })
  }, [])


  const UserDataDiv = ({data}) => {
    console.log(data.data)
    return data.data.map( (user, index)=>{
      return (
      <div class="container col-6 d-flex flex-row flex-wrap">
        <div>{user.title}</div>
        <div>{user.firstName}</div>
        <div>{user.lastName}</div>
      </div>)
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <div class="container-fluid m-2 h3">
          User data
        </div>
        <div className="container-fluid bg-light m-2">
          <div className="container">
            {loaded?<UserDataDiv data={data}/>:<h1>loading...</h1>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
