import './App.css';
import React from 'react';

const UserRow = ({data}) => {

  return data.map( (user, index)=>{
    return (
    <tr key={user.id}>
        <th scope="row">{index}</th>
        <td>{user.title.charAt(0).toUpperCase()}{user.title.slice(1,)}</td>
        <td>{user.firstName}</td>
        <td>{user.lastName}</td>
        <td><img className='row-6' src={user.picture} style={{"width":"150px"}}/></td>
        <td><button className='btn btn-outline-danger btn-blue ml-auto' onClick={()=>{deleteUser(user.id)}}>delete</button></td>
    </tr>
    )})
}

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users";
  var DEBUG = 1;

  const deleteUser = (user_id) => {
    setData( data.filter( (_, i)=>{ return user_id !== data[i].id} ))
    fetch( url + `/${user_id}`, { method: 'DELETE',}
    ).then(
      res => { if(DEBUG==1) console.log(res); return res.json()}
    ).then(
      json => { if(DEBUG==1) console.log(json)}
    ).catch( err => {console.log(err) })
  }

  React.useEffect(()=>{
    fetch( url ).then( res => { return res.json()})
    .then( received => {
      setData(received.data)
      setLoaded(true)
    })
  }, [])





  const UserTable = ({data}) => {
    if (data && data.length == 0){ return(<div className='h3 pt-5' style={{color:"#282c34"}}>all data is deleted...</div>) }
    else return(
    <table className='table'>
      <thead>
        <tr>
          <th scope="col" width="10%">#</th>
          <th scope="col" width="20%">Title</th>
          <th scope="col" width="20%">First</th>
          <th scope="col" width="20%">Last</th>
          <th scope="col" width="20%">Picture</th>
          <th scope="col" width="10%">Action</th>
        </tr>
      </thead>
      <tbody>
        <UserRow data={data}/>
      </tbody>
    </table>)
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"backgroundColor": "#ffffff","margin":"0px","minHeight":"80vh"}}>
          {loaded? <UserTable data={data}/> : <h3>loading...</h3>}
        </div>
      </header>
    </div>
  );
}

export default App;
