import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users"

  const deleteUser = (user_id) => {
    for(var i=0; i<data.data.length; i++){
      if( user_id == data.data[i].id ){
        console.log( user_id )
        setData
      }

    }
  }

  React.useEffect(()=>{
    fetch( url )
      .then( res => { return res.json()
      }).then( received => {
        setData(received.data)
        setLoaded(true)
      })
  }, [])


  const UserDataDiv = ({data}) => {
    console.log(data.data)
    return data.data.map( (user, index)=>{
      return (
      <div className="container col-lg-4 col-md-6 col-12 pt-3 mb-3 d-flex" id={user.id}>
        <div className="block container m-0 pt-3 pb-3 d-flex flex-row">
          <img className='row-6' src={user.picture} style={{"maxWidth":"50%"}}/>
          <div className='row-6 mr-auto pl-3 font-weight-light text-left'>
            <div>{user.title.charAt(0).toUpperCase()}{user.title.slice(1,)} {user.firstName}</div>
            <div>{user.lastName}</div>
            <div><button className='btn btn-danger float-right' onClick={()=>{deleteUser(user.id)}}>delete</button></div>
          </div>
        </div>
      </div>)
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"background-color": "#ffffff","margin":"0px","min-height":"80vh"}}>
          <div className="pt-4 d-flex flex-row flex-wrap container align-content-start" style={{color:"black"}}>
            {loaded? <UserDataDiv data={data}/>:<h1>loading...</h1>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
