import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users"

  const deleteUser = (user_id) => {
    setData( data.filter( (_, i)=>{ 
      return user_id !== data[i].id}
      ))
    fetch( url + `/${user_id}`, {
      method: 'DELETE',
    }).then(
      res => { console.log(res); return res.json()}
    ).then(
      res => {console.log(res)}
    ).catch( err => {console.log(err) })
  }

  React.useEffect(()=>{
    fetch( url ).then( res => { return res.json()})
    .then( received => {
      setData(received.data)
      setLoaded(true)
    })
  }, [])


  const UserDataDiv = ({data}) => {
    return data.map( (user, _)=>{
      return (
      <div className="container col-lg-4 col-md-6 col-12 pt-3 mb-3 d-flex" id={user.id} key={user.id}>
        <div className="block container m-0 pt-3 pb-3 d-flex flex-row">
          <img className='row-6' src={user.picture} style={{"width":"50%"}}/>
          <div className='row-6 pl-3 font-weight-light text-left flex-fill d-flex flex-column' style={{"fontSize":"20px"}}>
            <div>{user.title.charAt(0).toUpperCase()}{user.title.slice(1,)} {user.firstName}</div>
            <div className='mb-5'>{user.lastName}</div>
            <div className='d-flex align-items-end flex-fill'><button className='btn btn-outline-danger btn-blue ml-auto' onClick={()=>{deleteUser(user.id)}}>delete</button></div>
          </div>
        </div>
      </div>)
    })
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"backgroundColor": "#ffffff","margin":"0px","minHeight":"80vh"}}>
          <div className="pt-4 d-flex flex-row flex-wrap container" style={{color:"black"}}>
            {loaded? <UserDataDiv data={data}/>:<h3>loading...</h3>}
            {console.log(data)}
            {(data==null || data.length == 0)? <h3>all data is deleted...</h3>: <></>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
