      //     </div>
      //   </div>
      // </div>)


  const UserTable = ({data}) => {
    if (data && data.length == 0) return(<h3>all data is deleted...</h3>)
    else return(
    <table className='table'>
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">Title</th>
          <th scope="col">First</th>
          <th scope="col">Last</th>
          <th scope="col">Picture</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
        <UserRow data={data}/>
      </tbody>
    </table>)
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"backgroundColor": "#ffffff","margin":"0px","minHeight":"80vh"}}>
          {loaded? <UserTable data={data}/>:<h3>loading...</h3>}
        </div>
      </header>
    </div>
  );
}

export default App;
