import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState(null);
  const [loaded, setLoaded] = React.useState(false);
  const url = "http://localhost:3001/api/users";
  var DEBUG = 1;

  const deleteUser = (user_id) => {
    setData( data.filter( (_, i)=>{ return user_id !== data[i].id} ))
    fetch( url + `/${user_id}`, { method: 'DELETE',}
    ).then(
      res => { if(DEBUG==1) console.log("[DEBUG]"+res); return res.json()}
    ).then(
      res => { if(DEBUG==1) console.log("[DEBUG]"+res)}
    ).catch( err => {console.log(err) })
  }

  React.useEffect(()=>{
    fetch( url ).then( res => { return res.json()})
    .then( received => {
      setData(received.data)
      setLoaded(true)
    })
  }, [])


  const UserRow = ({data}) => {

    return data.map( (user, index)=>{
      return (
      <tr>
          <th scope="row">{index}</th>
          <td>{user.title.AtChar(0)+user.title}</td>
          <td>{user.firstName}</td>
          <td>{user.lastName}</td>
          <td><img className='row-6' src={user.picture} style={{"width":"50%"}}/></td>
          <td><button className='btn btn-outline-danger btn-blue ml-auto' onClick={()=>{deleteUser(user.id)}}>delete</button></td>
        </tr>
      )})
  }


  const UserTable = ({data}) => {
    if (data && data.length == 0) return(<h3>all data is deleted...</h3>)
    else return(
    <table className='table'>
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">Title</th>
          <th scope="col">First</th>
          <th scope="col">Last</th>
          <th scope="col">Picture</th>
          <th scope="col">Action</th>
        </tr>
      </thead>
      <tbody>
        <UserRow data={data}/>
      </tbody>
    </table>)
  }

  return (
    <div className="App">
      <header className="App-header">
        <div className="container m-2 h3 text-left pb-2 pt-4">
          User data
        </div>
        <div className="container w-100" style={{"backgroundColor": "#ffffff","margin":"0px","minHeight":"80vh"}}>
          {loaded? <UserTable data={data}/>:<h3>loading...</h3>}
        </div>
      </header>
    </div>
  );
}

export default App;
